(function($) {

	// https://remysharp.com/2010/07/21/throttling-function-calls
	function throttle(fn, threshhold, scope) {
		threshhold || (threshhold = 250);
		var last,
		deferTimer;
		return function () {
			var context = scope || this;

			var now = +new Date,
			args = arguments;
			if (last && now < last + threshhold) {
				// hold on to it
				clearTimeout(deferTimer);
				deferTimer = setTimeout(function () {
					last = now;
					fn.apply(context, args);
				}, threshhold);
			} else {
				last = now;
				fn.apply(context, args);
			}
		};
	}

	$(function() {

		$body = $('body');
		$siteHeader = $('.site-header');

		// -----------------------------------------------------------------------

		function updateScrolled() {
			if (window.pageYOffset > 100) {
				$body.addClass('scrolled');
			} else {
				$body.removeClass('scrolled');
			}
		}

		updateScrolled();

		$(window).on('scroll', throttle(function() {
			updateScrolled();
		}, 100));

		// -----------------------------------------------------------------------

		$('.site-header-toggle').on('click', function() {
			$siteHeader.toggleClass('menu-open');
		});

		// -----------------------------------------------------------------------

		var sr = ScrollReveal({ scale: 1 });
		sr.reveal('[data-reveal]');

		// -----------------------------------------------------------------------

		var $masonryRows = $('.masonry-rows').masonry({
			itemSelector: 'none',
			columnWidth: '.masonry-row',
			transitionDuration: 0,
		});

		var msnryRows = $masonryRows.data('masonry');

		$masonryRows.imagesLoaded(function() {
			$masonryRows.masonry('option', { itemSelector: '.masonry-row' });
			var $items = $masonryRows.find('.masonry-row');
			$masonryRows.masonry('appended', $items);
		});

		var $infiniteRows = $masonryRows.infiniteScroll({
			path: '.next-posts',
			append: '.masonry-row',
			outlayer: msnryRows,
			status: '.masonry-status',
		});

		$infiniteRows.on('last.infiniteScroll', function() {
			$('.masonry-status').remove();
		});

		$infiniteRows.on('append.infiniteScroll', function() {
			sr.sync();
		});

		// -----------------------------------------------------------------------

		var $masonryCols = $('.masonry-cols').masonry({
			itemSelector: 'none',
			columnWidth: '.masonry-col',
			gutter: 32,
			percentPosition: true,
			transitionDuration: 0,
		});

		var msnryCols = $masonryCols.data('masonry');

		$masonryCols.imagesLoaded(function() {
			$masonryCols.masonry('option', { itemSelector: '.masonry-col' });
			var $items = $masonryCols.find('.masonry-col');
			$masonryCols.masonry('appended', $items);
		});

		var $infiniteCols = $masonryCols.infiniteScroll({
			path: '.next-posts',
			append: '.masonry-col',
			outlayer: msnryCols,
			status: '.masonry-status',
		});

		$infiniteCols.on('last.infiniteScroll', function() {
			$('.masonry-status').remove();
		});

		$infiniteCols.on('append.infiniteScroll', function() {
			sr.sync();
		});

	});

})(window.jQuery);
